skins::skin* skins::getSkin(int id){
    if(id>=sizeof(skiny)/sizeof(skins::skin)||id<0){
        &(skiny[9]);
    }
    return &(skiny[id]); 
};
bool skins::haveSkin(int id,int64_t user,int64_t guild){
    if(id==0||id==9)return true;
    return db_p->get(user)->objGet(std::to_string(guild))->objGet("skins")->arrInclude(id);
};
void skins::giveSkin(int id,int64_t user,int64_t guild){
    if(!skins::haveSkin(id,user,guild)){
        return db_p->get(user)->objGet(std::to_string(guild))->objGet("skins")->arrAdd(id);
    }
};
void skins::setSkin(int id,int64_t user,int64_t guild){
    if(skins::haveSkin(id,user,guild)){
        db_p->get(user)->objGet(std::to_string(guild))->objGet("skin")->set(id);
    }
};
int skins::getSetSkin(int64_t user,int64_t guild){
    if(db_p->get(user)->objGet(std::to_string(guild))->objExist("skin")){
        return db_p->get(user)->objGet(std::to_string(guild))->objGet("skin")->intGet();
    }
    return 9;
};